    public DataPoint[] data(){
        int n = 235;
        DataPoint[] values = new DataPoint[n];     //creating an object of type DataPoint[] of size 'n'
        for(int i = 0; i < n; i++){
            DataPoint v = new DataPoint(waveArray[i], graphArray[i]);
            values[i] = v;
        }
        return values;
    }

    public void updateGraph(final int color, final int i) {
        handler.post(new Runnable() {
            public void run() {
                switch(color){
                    case 0:
                        deepbluei = i;
                        break;
                    case 1:
                        bluei = i;
                        break;
                    case 2:
                        greeni = i;
                        break;
                    case 3:
                        limei = i;
                        break;
                    case 4:
                        yellowi = i;
                        break;
                    case 5:
                        amberi = i;
                        break;
                    case 6:
                        redi = i;
                        break;
                    case 7:
                        white1i = i;
                        break;
                    case 8:
                        white2i = i;
                        break;
                }
                for (int z = 0; z < 235; z++) {
                    graphArray[z] = (deepblueArray[z] * deepbluei) + (blueArray[z] * bluei) + (greenArray[z] * greeni) + (limeArray[z] * limei) + (yellowArray[z] * yellowi) + (amberArray[z] * amberi) + (redArray[z] * redi) + (white1Array[z] * white1i) + (white2Array[z] * white2i);
                }
//                setXAxisValues();
                setData();
//                series.resetData(data());
//                graph.removeAllSeries();
//                graph.addSeries(series);
                try {
                    //set time in milis
                    Thread.sleep(100);
                }catch (Exception e){
                    e.printStackTrace();
                }
            }
        });
    }

    // This is used to store x-axis values
    private ArrayList<String> setXAxisValues(){
        ArrayList<String> xVals = new ArrayList<String>();
        for (int z = 0; z < 235; z++) {
            xVals.add(Integer.toString(waveArray[z]));
        }
        return xVals;
    }

    // This is used to store Y-axis values
    private ArrayList<Entry> setYAxisValues(){
        ArrayList<Entry> yVals = new ArrayList<Entry>();
        for (int z = 0; z < 235; z++) {
            yVals.add(new Entry((float)graphArray[z], z));
        }
        return yVals;
    }

